items.script:
-- Simple heartbeat processing
local hb = json.decode(result)
return hb.timestamp or os.time()

-- Map execution state to text
local states = { [false]='Idle', [true]='Running' }
return states[result]

-- Map program number to name
local progNum = tonumber(result)
local programs = {
  [1] = 'DRILL_PROGRAM_A',
  [2] = 'MILL_PROGRAM_B', 
  [3] = 'TAP_PROGRAM_C',
  [4] = 'BORE_PROGRAM_D'
}
return programs[progNum] or ('PROGRAM_' .. (progNum or 0))

-- Get the most recent product name
if result and #result > 0 then
  return result[1];
end
return nil;

-- Calculate total quantity from all rows
local total = 0;
if result then
  for i = 1, #result do
    if result[i] then
      total = total + (tonumber(result[i]) or 0);
    end
  end
end
return total;

-- Count active orders
local active_count = 0;
if result then
  for i = 1, #result do
    if result[i] == 'Active' then
      active_count = active_count + 1;
    end
  end
end
return active_count;

-- Count pending orders
local pending_count = 0;
if result then
  for i = 1, #result do
    if result[i] == 'Pending' then
      pending_count = pending_count + 1;
    end
  end
end
return pending_count;

-- Create comprehensive order summary
local summary = {};
if result then
  for i = 1, #result do
    if result[i] then
      emit('./Order_' .. result[i] .. '_Status', cache('Status', {})[i] or 'Unknown');
      emit('./Order_' .. result[i] .. '_Quantity', cache('Quantity', {})[i] or 0);
    end
  end
  summary.total_orders = #result;
  summary.timestamp = os.date('%Y-%m-%d %H:%M:%S');
end
return summary;

local sum = 0;
local count = 0;
for i = 1, #result do
  sum = sum + (tonumber(result[i]) or 0);
  count = count + 1;
end
return count > 0 and (sum / count) or 0;

-- Extract value from MTConnect data item
if result and #result > 0 then
  return result[1].Value;
end
return nil;

-- Process spindle speed data
if result and #result > 0 then
  local speed = tonumber(result[1].Value);
  if speed then
    -- Emit additional derived values
    emit('./SpindleSpeedRPM', speed);
    if speed > 1000 then
      emit('./SpindleStatus', 'HIGH_SPEED');
    elseif speed > 0 then
      emit('./SpindleStatus', 'NORMAL');
    else
      emit('./SpindleStatus', 'STOPPED');
    end
    return speed;
  end
end
return 0;

-- Map execution states
if result and #result > 0 then
  local state = result[1].Value;
  local execution_map = {
    ['ACTIVE'] = 'Running',
    ['READY'] = 'Ready',
    ['INTERRUPTED'] = 'Stopped',
    ['STOPPED'] = 'Idle'
  };
  return execution_map[state] or state;
end
return 'Unknown';

-- Program name changes less frequently
if result and #result > 0 then
  return result[1].Value;
end
return nil;

if result and #result > 0 then
  local tool = result[1].Value;
  if tool then
    emit('./CurrentTool', tool);
    -- Emit tool change event if different from previous
    local previous_tool = cache('./PreviousTool', nil);
    if previous_tool and previous_tool ~= tool then
      emit('./ToolChanged', true);
    else
      emit('./ToolChanged', false);
    end
    set('PreviousTool', tool);
    return tool;
  end
end
return nil;

-- Process availability data
if result and #result > 0 then
  local avail = result[1].Value;
  local availability_map = {
    ['AVAILABLE'] = true,
    ['UNAVAILABLE'] = false
  };
  local is_available = availability_map[avail];
  if is_available ~= nil then
    emit('./IsAvailable', is_available);
    emit('./AvailabilityText', avail);
    return is_available;
  end
end
return false;

-- Collect multiple axis positions
local x_pos = nil;
local y_pos = nil;
local z_pos = nil;

if result and #result > 0 then
  x_pos = tonumber(result[1].Value);
end

-- Get Y and Z axis positions from cache
y_pos = tonumber(cache('mtconnect_source/y_axis', 0));
z_pos = tonumber(cache('mtconnect_source/z_axis', 0));

if x_pos then
  local position_data = {
    x = x_pos,
    y = y_pos,
    z = z_pos,
    timestamp = os.time()
  };

  emit('./MachinePosition', position_data);
  return x_pos;
end
return nil;

if result and #result > 0 then
  return tonumber(result[1].Value);
end
return 0;

if result and #result > 0 then
  return tonumber(result[1].Value);
end
return 0;

-- Process alarm information
if result and #result > 0 then
  local alarm_data = result[1];
  if alarm_data.Value and alarm_data.Value ~= '' then
    emit('./AlarmActive', true);
    emit('./AlarmCode', alarm_data.Value);
    emit('./AlarmText', alarm_data.Value);
    return {
      active = true,
      code = alarm_data.Value,
      timestamp = alarm_data.Timestamp or os.time()
    };
  else
    emit('./AlarmActive', false);
    return { active = false };
  end
end
return { active = false };

-- Process New York daily weather
if result and result.properties and result.properties.periods then
  local current_period = result.properties.periods[1];
  if current_period then
    local temp = current_period.temperature;
    local conditions = current_period.shortForecast;
    local wind_speed = current_period.windSpeed;

    -- Emit individual weather components
    emit('./NYC_Temperature', temp);
    emit('./NYC_Conditions', conditions);
    emit('./NYC_WindSpeed', wind_speed);
    emit('./NYC_Humidity', current_period.relativeHumidity and current_period.relativeHumidity.value or nil);

    -- Create weather summary
    local weather_summary = {
      location = 'New York City',
      temperature = temp,
      conditions = conditions,
      wind_speed = wind_speed,
      forecast_time = current_period.startTime
    };

    return weather_summary;
  end
end
return nil;

-- System timestamp from OPC server
if result then
  emit('./ServerTime', result);
  return result;
end
return nil;

-- PLC heartbeat counter
local heartbeat = tonumber(result);
if heartbeat then
  local prev_heartbeat = cache('PreviousHeartbeat', 0);
  if heartbeat ~= prev_heartbeat then
    emit('./PlcAlive', true);
    emit('./HeartbeatCounter', heartbeat);
  else
    emit('./PlcAlive', false);
  end
  set('PreviousHeartbeat', heartbeat);
  return heartbeat;
end
return 0;

-- Production counter with shift calculation
local count = tonumber(result) or 0;
local shift_start = cache('ShiftStartCount', count);
local shift_parts = count - shift_start;

emit('./TotalParts', count);
emit('./ShiftParts', shift_parts);

-- Calculate production rate (parts per minute)
local current_time = os.time();
local last_time = cache('LastCountTime', current_time);
local last_count = cache('LastCount', count);
local time_diff = current_time - last_time;

if time_diff >= 60 then  -- Calculate rate every minute
  local parts_diff = count - last_count;
  local rate = parts_diff / (time_diff / 60);  -- parts per minute
  emit('./ProductionRate', rate);
  set('LastCountTime', current_time);
  set('LastCount', count);
end

return count;

-- Machine running status
local is_running = result;
if is_running then
  emit('./MachineState', 'RUNNING');
  emit('./IsProducing', true);
else
  emit('./MachineState', 'STOPPED');
  emit('./IsProducing', false);
end
return is_running;

-- Temperature monitoring with alerts
local temp = tonumber(result);
if temp then
  emit('./CurrentTemp', temp);

  -- Temperature alerts
  if temp > 80 then
    emit('./TempAlert', 'CRITICAL');
    emit('./TempStatus', 'OVERHEATED');
  elseif temp > 70 then
    emit('./TempAlert', 'WARNING');
    emit('./TempStatus', 'HIGH');
  else
    emit('./TempAlert', 'NORMAL');
    emit('./TempStatus', 'NORMAL');
  end

  return temp;
end
return 0;

-- Active alarm monitoring
local alarm_active = result;
if alarm_active then
  emit('./AlarmActive', true);
  emit('./MachineHealth', 'FAULT');
else
  emit('./AlarmActive', false);
  emit('./MachineHealth', 'NORMAL');
end
return alarm_active;

-- Motor speed with efficiency calculation
local speed = tonumber(result) or 0;
local target_speed = 1800;  -- RPM
local efficiency = 0;

if target_speed > 0 then
  efficiency = (speed / target_speed) * 100;
end

emit('./MotorRPM', speed);
emit('./MotorEfficiency', efficiency);

-- Speed status
if speed > target_speed * 1.1 then
  emit('./SpeedStatus', 'OVERSPEED');
elseif speed < target_speed * 0.9 then
  emit('./SpeedStatus', 'UNDERSPEED');
else
  emit('./SpeedStatus', 'NORMAL');
end

return speed;

-- Quality monitoring (check every 5 cycles)
local defects = tonumber(result) or 0;
local total_parts = tonumber(cache('opcda_source/ProductionCount', 0));
local defect_rate = 0;

if total_parts > 0 then
  defect_rate = (defects / total_parts) * 100;
end

emit('./DefectCount', defects);
emit('./DefectRate', defect_rate);

-- Quality alerts
if defect_rate > 5 then
  emit('./QualityAlert', 'HIGH_DEFECT_RATE');
elseif defect_rate > 2 then
  emit('./QualityAlert', 'WARNING');
else
  emit('./QualityAlert', 'NORMAL');
end

return {
  defects = defects,
  rate = defect_rate,
  total_parts = total_parts
};

-- Recipe and setup information (check every 10 cycles)
local recipe = result;
if recipe then
  emit('./ActiveRecipe', recipe);
  emit('./RecipeChanged', recipe ~= cache('PreviousRecipe', ''));
  set('PreviousRecipe', recipe);
  return recipe;
end
return 'No Recipe';

-- Server timestamp
if result then
  emit('./ServerTime', result);
  return result;
end
return nil;

-- Random simulation value
local value = tonumber(result);
if value then
  -- Emit alerts based on value ranges
  if value > 80 then
    emit('./RandomAlert', 'HIGH');
  elseif value < 20 then
    emit('./RandomAlert', 'LOW');
  else
    emit('./RandomAlert', 'NORMAL');
  end
  return value;
end
return 0;

-- Process temperature monitoring
local temp = tonumber(result);
if temp then
  emit('./TempCelsius', temp);
  emit('./TempFahrenheit', (temp * 9/5) + 32);

  -- Temperature control logic
  if temp > 100 then
    emit('./TempAlert', 'CRITICAL_HIGH');
    emit('./CoolingRequired', true);
  elseif temp > 85 then
    emit('./TempAlert', 'WARNING_HIGH');
    emit('./CoolingRequired', false);
  elseif temp < 10 then
    emit('./TempAlert', 'CRITICAL_LOW');
    emit('./HeatingRequired', true);
  else
    emit('./TempAlert', 'NORMAL');
    emit('./CoolingRequired', false);
    emit('./HeatingRequired', false);
  end

  return temp;
end
return 0;

-- Motor running status
local running = result;
if running then
  emit('./MotorState', 'RUNNING');
  emit('./IsOperational', true);
else
  emit('./MotorState', 'STOPPED');
  emit('./IsOperational', false);
end
return running;

-- Production counter with rate calculation
local count = tonumber(result) or 0;
local previous_count = cache('PreviousCount', count);
local current_time = os.time();
local last_time = cache('LastCountTime', current_time);

-- Calculate production rate
local time_diff = current_time - last_time;
if time_diff >= 60 then  -- Every minute
  local parts_produced = count - previous_count;
  local rate = parts_produced / (time_diff / 60);  -- parts per minute

  emit('./ProductionRate', rate);
  emit('./PartsThisMinute', parts_produced);

  set('PreviousCount', count);
  set('LastCountTime', current_time);
end

emit('./TotalParts', count);
return count;

-- Quality monitoring (every 3 cycles)
local defect_rate = tonumber(result) or 0;

emit('./DefectPercentage', defect_rate);

-- Quality alerts
if defect_rate > 5.0 then
  emit('./QualityStatus', 'CRITICAL');
  emit('./QualityAlert', 'HIGH_DEFECT_RATE');
elseif defect_rate > 2.0 then
  emit('./QualityStatus', 'WARNING');
  emit('./QualityAlert', 'ELEVATED_DEFECTS');
else
  emit('./QualityStatus', 'GOOD');
  emit('./QualityAlert', 'NORMAL');
end

return defect_rate;

-- Active alarm count
local alarm_count = tonumber(result) or 0;

emit('./ActiveAlarmCount', alarm_count);

if alarm_count > 0 then
  emit('./MachineHealth', 'FAULT');
  emit('./AlarmsActive', true);
else
  emit('./MachineHealth', 'NORMAL');
  emit('./AlarmsActive', false);
end

return alarm_count;

-- Energy monitoring
local power_kw = tonumber(result) or 0;

emit('./PowerKW', power_kw);
emit('./PowerWatts', power_kw * 1000);

-- Calculate energy consumption
local current_time = os.time();
local last_time = cache('LastEnergyTime', current_time);
local time_diff_hours = (current_time - last_time) / 3600;

if time_diff_hours >= 0.25 then  -- Every 15 minutes
  local energy_consumed = power_kw * time_diff_hours;  -- kWh
  local total_energy = cache('TotalEnergy', 0) + energy_consumed;

  emit('./EnergyThisPeriod', energy_consumed);
  emit('./TotalEnergyKWh', total_energy);

  set('TotalEnergy', total_energy);
  set('LastEnergyTime', current_time);
end

-- Power alerts
if power_kw > 50 then
  emit('./PowerAlert', 'HIGH_CONSUMPTION');
elseif power_kw < 5 then
  emit('./PowerAlert', 'LOW_CONSUMPTION');
else
  emit('./PowerAlert', 'NORMAL');
end

return power_kw;

-- Overall equipment status (every 5 cycles)
local status = result;
local status_map = {
  ['0'] = 'STOPPED',
  ['1'] = 'STARTING',
  ['2'] = 'RUNNING',
  ['3'] = 'STOPPING',
  ['4'] = 'FAULT',
  ['5'] = 'MAINTENANCE'
};

local status_text = status_map[tostring(status)] or 'UNKNOWN';
emit('./EquipmentState', status_text);

-- Availability calculation
local is_available = (status_text == 'RUNNING' or status_text == 'STARTING');
emit('./IsAvailable', is_available);

return {
  code = status,
  text = status_text,
  available = is_available
};

-- Recipe information (every 10 cycles)
local recipe = result;
if recipe then
  emit('./CurrentRecipe', recipe);

  -- Detect recipe changes
  local previous_recipe = cache('PreviousRecipe', '');
  if recipe ~= previous_recipe then
    emit('./RecipeChanged', true);
    emit('./RecipeChangeTime', os.date('%Y-%m-%d %H:%M:%S'));
    set('PreviousRecipe', recipe);
  else
    emit('./RecipeChanged', false);
  end

  return recipe;
end
return 'No Recipe';

-- Count active orders
local active_count = 0;
local processing_count = 0;

if result then
  for i = 1, #result do
    if result[i] == 'Active' then
      active_count = active_count + 1;
    elseif result[i] == 'Processing' then
      processing_count = processing_count + 1;
    end
  end
end

emit('./ActiveOrders', active_count);
emit('./ProcessingOrders', processing_count);
emit('./TotalOrders', active_count + processing_count);

return {
  active = active_count,
  processing = processing_count,
  total = active_count + processing_count
};

-- Calculate total quantity across all orders
local total = 0;
local max_qty = 0;
local min_qty = math.huge;

if result then
  for i = 1, #result do
    local qty = tonumber(result[i]) or 0;
    total = total + qty;
    if qty > max_qty then max_qty = qty; end
    if qty < min_qty then min_qty = qty; end
  end
end

if min_qty == math.huge then min_qty = 0; end

emit('./TotalQuantity', total);
emit('./MaxOrderQuantity', max_qty);
emit('./MinOrderQuantity', min_qty);

if #result > 0 then
  emit('./AvgOrderQuantity', total / #result);
end

return total;

-- Analyze product mix
local product_counts = {};
local unique_products = 0;

if result then
  for i = 1, #result do
    local product = result[i];
    if product then
      if product_counts[product] then
        product_counts[product] = product_counts[product] + 1;
      else
        product_counts[product] = 1;
        unique_products = unique_products + 1;
      end
    end
  end
end

emit('./UniqueProducts', unique_products);

-- Emit top products
local top_product = nil;
local top_count = 0;
for product, count in pairs(product_counts) do
  emit('./Product_' .. product .. '_Count', count);
  if count > top_count then
    top_count = count;
    top_product = product;
  end
end

if top_product then
  emit('./TopProduct', top_product);
  emit('./TopProductCount', top_count);
end

return product_counts;

-- Operator workload analysis (every 2 cycles)
local operator_loads = {};

if result then
  for i = 1, #result do
    local operator = result[i];
    if operator then
      operator_loads[operator] = (operator_loads[operator] or 0) + 1;
    end
  end
end

-- Emit workload per operator
for operator, load in pairs(operator_loads) do
  emit('./Operator_' .. operator .. '_Orders', load);
end

return operator_loads;

-- Calculate order aging
local current_time = os.time();
local aged_orders = 0;
local urgent_orders = 0;
local total_age_hours = 0;

if result then
  for i = 1, #result do
    local created_date = result[i];
    if created_date then
      -- Parse PostgreSQL timestamp (simplified)
      local year, month, day, hour, min, sec = created_date:match('(%d+)-(%d+)-(%d+) (%d+):(%d+):(%d+)');
      if year then
        local created_time = os.time({
          year = tonumber(year),
          month = tonumber(month),
          day = tonumber(day),
          hour = tonumber(hour),
          min = tonumber(min),
          sec = tonumber(sec)
        });

        local age_seconds = current_time - created_time;
        local age_hours = age_seconds / 3600;
        total_age_hours = total_age_hours + age_hours;

        if age_hours > 24 then
          aged_orders = aged_orders + 1;
        end
        if age_hours > 48 then
          urgent_orders = urgent_orders + 1;
        end
      end
    end
  end
end

emit('./AgedOrders', aged_orders);
emit('./UrgentOrders', urgent_orders);

if #result > 0 then
  emit('./AvgOrderAgeHours', total_age_hours / #result);
end

return {
  aged = aged_orders,
  urgent = urgent_orders,
  avg_age_hours = #result > 0 and (total_age_hours / #result) or 0
};

-- Read cached PLC part count
local count = tonumber(result);
if count then
  -- Calculate shift production
  local shift_start = cache('ShiftStartCount', count);
  local shift_parts = count - shift_start;

  emit('./TotalParts', count);
  emit('./ShiftParts', shift_parts);

  -- Production rate calculation
  local current_time = os.time();
  local last_time = cache('LastCountTime', current_time);
  local last_count = cache('LastCount', count);
  local time_diff = current_time - last_time;

  if time_diff >= 60 then  -- Every minute
    local parts_diff = count - last_count;
    local rate = parts_diff / (time_diff / 60);  -- parts per minute
    emit('./ProductionRate', rate);
    set('LastCountTime', current_time);
    set('LastCount', count);
  end

  return count;
end
return 0;

-- Machine status from Redis cache
local status = result;
if status then
  local status_map = {
    ['1'] = 'RUNNING',
    ['0'] = 'STOPPED',
    ['2'] = 'FAULT',
    ['3'] = 'MAINTENANCE'
  };

  local status_text = status_map[tostring(status)] or 'UNKNOWN';
  emit('./MachineState', status_text);
  emit('./IsRunning', status_text == 'RUNNING');

  return status_text;
end
return 'OFFLINE';

-- Temperature sensor data
local temp = tonumber(result);
if temp then
  emit('./Temperature', temp);

  -- Temperature alerts
  if temp > 85 then
    emit('./TempAlert', 'CRITICAL');
    emit('./CoolingRequired', true);
  elseif temp > 75 then
    emit('./TempAlert', 'WARNING');
    emit('./CoolingRequired', false);
  else
    emit('./TempAlert', 'NORMAL');
    emit('./CoolingRequired', false);
  end

  -- Store min/max for the day
  local current_min = cache('DailyMinTemp', temp);
  local current_max = cache('DailyMaxTemp', temp);

  if temp < current_min then
    set('DailyMinTemp', temp);
    emit('./DailyMinTemp', temp);
  end

  if temp > current_max then
    set('DailyMaxTemp', temp);
    emit('./DailyMaxTemp', temp);
  end

  return temp;
end
return nil;

-- System metrics (every 3 cycles)
if result then
  -- Parse JSON metrics if stored as JSON string
  local metrics = nil;
  if type(result) == 'string' then
    metrics = from_json(result);
  else
    metrics = result;
  end

  if metrics then
    emit('./CPUUsage', metrics.cpu or 0);
    emit('./MemoryUsage', metrics.memory or 0);
    emit('./DiskUsage', metrics.disk or 0);

    -- System health alerts
    local health_status = 'GOOD';
    if metrics.cpu > 90 or metrics.memory > 90 or metrics.disk > 90 then
      health_status = 'CRITICAL';
    elseif metrics.cpu > 75 or metrics.memory > 75 or metrics.disk > 75 then
      health_status = 'WARNING';
    end

    emit('./SystemHealth', health_status);
    return metrics;
  end
end
return nil;

-- Active user sessions count (every 5 cycles)
local sessions = tonumber(result) or 0;
emit('./ActiveSessions', sessions);

-- Session load alerts
if sessions > 100 then
  emit('./SessionAlert', 'HIGH_LOAD');
elseif sessions > 50 then
  emit('./SessionAlert', 'MODERATE_LOAD');
else
  emit('./SessionAlert', 'NORMAL');
end

return sessions;

-- Cache performance monitoring
local hit_ratio = tonumber(result);
if hit_ratio then
  emit('./CacheHitRatio', hit_ratio);

  -- Performance alerts
  if hit_ratio < 0.7 then
    emit('./CachePerformance', 'POOR');
  elseif hit_ratio < 0.9 then
    emit('./CachePerformance', 'FAIR');
  else
    emit('./CachePerformance', 'EXCELLENT');
  end

  return hit_ratio * 100;  -- Return as percentage
end
return 0;

-- Processing queue monitoring
local queue_length = tonumber(result) or 0;
emit('./QueueLength', queue_length);

-- Queue alerts
if queue_length > 1000 then
  emit('./QueueAlert', 'CRITICAL');
  emit('./QueueStatus', 'OVERLOADED');
elseif queue_length > 500 then
  emit('./QueueAlert', 'WARNING');
  emit('./QueueStatus', 'HIGH');
else
  emit('./QueueAlert', 'NORMAL');
  emit('./QueueStatus', 'NORMAL');
end

-- Estimate processing time
local processing_rate = cache('ProcessingRate', 10);  -- items per minute
if queue_length > 0 and processing_rate > 0 then
  local estimated_time = queue_length / processing_rate;
  emit('./EstimatedProcessingTime', estimated_time);
end

return queue_length;

-- Error monitoring
local errors = tonumber(result) or 0;
emit('./ErrorCount', errors);

-- Error rate alerts
if errors > 100 then
  emit('./ErrorLevel', 'CRITICAL');
  emit('./SystemStability', 'UNSTABLE');
elseif errors > 50 then
  emit('./ErrorLevel', 'HIGH');
  emit('./SystemStability', 'DEGRADED');
elseif errors > 10 then
  emit('./ErrorLevel', 'MODERATE');
  emit('./SystemStability', 'STABLE');
else
  emit('./ErrorLevel', 'LOW');
  emit('./SystemStability', 'STABLE');
end

return errors;

-- Configuration monitoring (every 10 cycles)
if result then
  local config = nil;

  -- Handle JSON configuration
  if type(result) == 'string' then
    config = from_json(result);
  else
    config = result;
  end

  if config then
    -- Emit individual configuration values
    for key, value in pairs(config) do
      emit('./Config_' .. key, value);
    end

    -- Check for configuration changes
    local config_hash = tostring(config):len();  -- Simple hash
    local previous_hash = cache('ConfigHash', config_hash);

    if config_hash ~= previous_hash then
      emit('./ConfigurationChanged', true);
      emit('./ConfigChangeTime', os.date('%Y-%m-%d %H:%M:%S'));
      set('ConfigHash', config_hash);
    else
      emit('./ConfigurationChanged', false);
    end

    return config;
  end
end
return nil;

local count = cache('counter', 0) + 1;
set('counter', count);
return count;

local base = 72;
local variation = math.random(-5, 5);
return base + variation;

-- Machine running status
local running = result;
if running then
  emit('./MachineState', 'RUNNING');
  emit('./IsOperational', true);
else
  emit('./MachineState', 'STOPPED');
  emit('./IsOperational', false);
end
return running;

-- Emergency stop input
local estop = result;
if estop then
  emit('./SafetyStatus', 'E_STOP_ACTIVE');
  emit('./SafetyAlert', 'CRITICAL');
else
  emit('./SafetyStatus', 'NORMAL');
  emit('./SafetyAlert', 'NONE');
end
return estop;

-- Production counter with rate calculation
local count = tonumber(result) or 0;
local previous_count = cache('PreviousCount', count);
local current_time = os.time();
local last_time = cache('LastCountTime', current_time);

-- Calculate production rate
local time_diff = current_time - last_time;
if time_diff >= 60 then  -- Every minute
  local parts_produced = count - previous_count;
  local rate = parts_produced / (time_diff / 60);  -- parts per minute

  emit('./ProductionRate', rate);
  emit('./PartsThisMinute', parts_produced);

  set('PreviousCount', count);
  set('LastCountTime', current_time);
end

emit('./TotalParts', count);

-- Shift production calculation
local shift_start = cache('ShiftStartCount', count);
emit('./ShiftParts', count - shift_start);

return count;

-- Motor speed monitoring
local speed = tonumber(result) or 0;
local target_speed = 1800;  -- RPM

emit('./MotorRPM', speed);

-- Speed status and efficiency
local efficiency = 0;
if target_speed > 0 then
  efficiency = (speed / target_speed) * 100;
end

emit('./MotorEfficiency', efficiency);

-- Speed alerts
if speed > target_speed * 1.1 then
  emit('./SpeedAlert', 'OVERSPEED');
elseif speed < target_speed * 0.8 then
  emit('./SpeedAlert', 'UNDERSPEED');
else
  emit('./SpeedAlert', 'NORMAL');
end

return speed;

-- Process temperature monitoring
local temp = tonumber(result) or 0;

emit('./Temperature', temp);
emit('./TempFahrenheit', (temp * 9/5) + 32);

-- Temperature control and alerts
if temp > 85 then
  emit('./TempAlert', 'CRITICAL_HIGH');
  emit('./CoolingRequired', true);
elseif temp > 75 then
  emit('./TempAlert', 'WARNING_HIGH');
  emit('./CoolingRequired', false);
elseif temp < 10 then
  emit('./TempAlert', 'CRITICAL_LOW');
  emit('./HeatingRequired', true);
else
  emit('./TempAlert', 'NORMAL');
  emit('./CoolingRequired', false);
  emit('./HeatingRequired', false);
end

-- Track daily min/max
local daily_min = cache('DailyMinTemp', temp);
local daily_max = cache('DailyMaxTemp', temp);

if temp < daily_min then
  set('DailyMinTemp', temp);
  emit('./DailyMinTemp', temp);
end

if temp > daily_max then
  set('DailyMaxTemp', temp);
  emit('./DailyMaxTemp', temp);
end

return temp;

-- Active recipe number (check every 5 cycles)
local recipe_num = tonumber(result) or 0;
local recipe_map = {
  [1] = 'Product_A',
  [2] = 'Product_B',
  [3] = 'Product_C',
  [0] = 'No_Recipe'
};

local recipe_name = recipe_map[recipe_num] or 'Unknown';
emit('./RecipeNumber', recipe_num);
emit('./RecipeName', recipe_name);

-- Detect recipe changes
local previous_recipe = cache('PreviousRecipe', recipe_num);
if recipe_num ~= previous_recipe then
  emit('./RecipeChanged', true);
  emit('./RecipeChangeTime', os.date('%Y-%m-%d %H:%M:%S'));
  set('PreviousRecipe', recipe_num);
else
  emit('./RecipeChanged', false);
end

return {
  number = recipe_num,
  name = recipe_name
};

-- Alarm word processing
local alarm_word = tonumber(result) or 0;
local active_alarms = {};
local alarm_count = 0;

-- Check individual alarm bits
local alarm_names = {
  [0] = 'Motor_Fault',
  [1] = 'Overheating',
  [2] = 'Low_Pressure',
  [3] = 'Door_Open',
  [4] = 'Emergency_Stop',
  [5] = 'Material_Empty'
};

for bit = 0, 15 do
  local is_active = (alarm_word & (1 << bit)) ~= 0;
  local alarm_name = alarm_names[bit] or ('Alarm_' .. bit);

  emit('./Alarm_' .. alarm_name, is_active);

  if is_active then
    table.insert(active_alarms, alarm_name);
    alarm_count = alarm_count + 1;
  end
end

emit('./ActiveAlarmCount', alarm_count);
emit('./AnyAlarmsActive', alarm_count > 0);

if alarm_count > 0 then
  emit('./MachineHealth', 'FAULT');
else
  emit('./MachineHealth', 'NORMAL');
end

return {
  word = alarm_word,
  count = alarm_count,
  alarms = active_alarms
};

-- Quality monitoring (every 3 cycles)
local defect_count = tonumber(result) or 0;
local total_parts = tonumber(cache('siemens_s7_source/ProductionCounter', 1));
local defect_rate = 0;

if total_parts > 0 then
  defect_rate = (defect_count / total_parts) * 100;
end

emit('./DefectCount', defect_count);
emit('./DefectRate', defect_rate);

-- Quality status
if defect_rate > 5 then
  emit('./QualityStatus', 'POOR');
elseif defect_rate > 2 then
  emit('./QualityStatus', 'ACCEPTABLE');
else
  emit('./QualityStatus', 'EXCELLENT');
end

return {
  defects = defect_count,
  rate = defect_rate,
  total = total_parts
};

-- Cycle time in milliseconds
local cycle_ms = tonumber(result) or 0;
local cycle_seconds = cycle_ms / 1000;

emit('./CycleTimeMS', cycle_ms);
emit('./CycleTimeSeconds', cycle_seconds);

-- Cycle time analysis
local target_cycle = 30;  -- seconds
local efficiency = 0;

if cycle_seconds > 0 then
  efficiency = (target_cycle / cycle_seconds) * 100;
end

emit('./CycleEfficiency', efficiency);

-- Performance alerts
if cycle_seconds > target_cycle * 1.2 then
  emit('./CycleAlert', 'SLOW');
elseif cycle_seconds < target_cycle * 0.8 then
  emit('./CycleAlert', 'FAST');
else
  emit('./CycleAlert', 'NORMAL');
end

return cycle_seconds;

-- Convert temperature if needed
local temp_celsius = tonumber(result);
if temp_celsius then
  return temp_celsius / 1000;  -- Convert from millidegrees
end
return result;

